CREATE TABLE diety (name text,
                    alignment alignment,
                    title text,
                    pantheon text,
                    symbol text,
                    source text REFERENCES book (abbreviation),
                    reprinted boolean,
                    page smallint,
                    description text,
                    PRIMARY KEY (name, pantheon));



CREATE TYPE domain AS ENUM ('Arcana',
                            'Death',
                            'Forge',
                            'Grave',
                            'Knowledge',
                            'Life',
                            'Light',
                            'Nature',
                            'Tempest',
                            'Trickery',
                            'War');

CREATE TABLE dietyDomains (diety text,
                           pantheon text,
                           domain domain NOT NULL,
                           FOREIGN KEY (diety, pantheon) REFERENCES diety (name, pantheon),
                           PRIMARY KEY (diety, pantheon, domain));

