CREATE TYPE rarity AS ENUM ('Common',
                            'Uncommon',
                            'Rare',
                            'Very Rare',
                            'Legendary',
                            'Artifact',
                            'Unknown');

CREATE TYPE itemType AS ENUM ('Ammunition',
                              'Artisan Tool',
                              'Explosive',
                              'Adventuring Gear',
                              'Gaming Set',
                              'Heavy Armor',
                              'Instrument',
                              'Light Armor',
                              'Melee Weapon',
                              'Medium Armor',
                              'Mount',
                              'Generic Variant',
                              'Potion',
                              'Ranged Weapon',
                              'Rod',
                              'Ring',
                              'Shield',
                              'Scroll',
                              'Spellcasting Focus',
                              'Tool',
                              'Tack and Harness',
                              'Trade Good',
                              'Treasure',
                              'Vehicle',
                              'Wand');

CREATE TYPE damageType AS ENUM ('bludgeoning',
                                'necrotic',
                                'piercing',
                                'radiant',
                                'slashing');

CREATE TYPE weaponType AS ENUM ('Martial',
                                'Simple');

CREATE TYPE weaponRange AS ENUM ('Melee',
                                 'Ranged',
                                 'Mixed');

CREATE TABLE item (name text PRIMARY KEY,
                   type itemType,
                   rarity rarity,
                   source text REFERENCES book (abbreviation),
                   value gp,
                   weight smallint,
                   page smallint,
                   description text,
                   randomBeneficialProperties smallint,
                   randomDetrimentalProperties smallint,
                   ac smallint,
                   axe boolean,
                   weapon boolean,
                   weaponCategory weaponType,
                   damage1 text,
                   damage2 text,
                   damageBonus smallint,
                   hitBonus smallint,
                   range lseg);


CREATE TABLE itemAction(item text REFERENCES item (name),
                        difficultyClass smallint,
                        name text,
                        description text);

